import React from "react";
export default function Card(props){
    let badgeText
    if(props.item.openSpots === 0){
        badgeText = "SOLD OUT"
    }
    else if(props.item.openSpots === "Online"){
        badgeText = "ONLINE"
    }
    return(
        <div className="card">
            {badgeText && <div className = "card--badge">SOLD-OUT BADGE</div>}
            <img src = {props.item.Coverimg} className = "card--image"/>
            <div className="card--stats">
                <span><img src = "/images/Star.png" width = "20px" className="card--star"/></span>
                <span>{props.rating}</span>
                <span className="gray">({props.item.reviewCount}) â€¢ </span>
                <span className="gray">{props.item.location}</span>
            </div>
            <p className="card--title">{props.item.title}</p>
            <p> <span className="bold">From {props.item.price} per person</span></p>
        </div>
    )
}
